'use strict';

module.exports = (a) => {
	let o = [];
	Array.prototype.randomElement = function() {
	    return this[Math.floor(Math.random() * this.length)]
	};
	return function ran_array() {
	    const b = a.randomElement();
	    return a.length == o.length && (o = []), o.includes(b) ? ran_array() : (o.push(b), b)
	}
}